export class Pelicula {
  constructor(nombre, tipo, fechaEstreno, cantPublico, fotoPortada) {
    this.id = this.generarId();
    this.nombre = nombre;
    this.tipo = tipo;
    this.fechaEstreno = fechaEstreno;
    this.cantPublico = cantPublico;
    this.fotoPortada = fotoPortada;
  }
  generarId() {
    return 1;
  }
  guardarEnLocalStorage() {
    let listaPeliculas;
    listaPeliculas = Pelicula.obtenerLocalStorage();
    listaPeliculas.push(this);
    localStorage.setItem("listaPeliculas", JSON.stringify(listaPeliculas));
  }
  static obtenerLocalStorage() {
    let jsonCargado = null;
    let peliculas = localStorage.getItem("listaPeliculas");
    if (peliculas != null) {
      jsonCargado = JSON.parse(peliculas);
    } else {
      jsonCargado = Array();
    }
    return jsonCargado;
  }
  static borrarStorage() {
    localStorage.removeItem("listaPeliculas");
  }
}
