export class Pelicula 
{
    id : number;
    nombre : string;
    tipo : genero;
    fechaEstreno : Date;
    cantPublico : number;
    fotoPortada : string;

    constructor(nombre : string, tipo : genero, fechaEstreno: Date, cantPublico : number, fotoPortada: string)
    {
        this.id = this.generarId();
        this.nombre = nombre;
        this.tipo = tipo;
        this.fechaEstreno = fechaEstreno;
        this.cantPublico = cantPublico;
        this.fotoPortada = fotoPortada;

    }

    generarId()
    {
        return 1;
    }

    public guardarEnLocalStorage()
    {
        let listaUsuarios : Array<Pelicula>;

        listaUsuarios = Pelicula.obtenerLocalStorage();

        listaUsuarios.push(this);

        localStorage.setItem("listaUsuarios", JSON.stringify(listaUsuarios));
    }

    public static obtenerLocalStorage()
    {
        let jsonCargado = null;
        let usuarios : string | null = localStorage.getItem("listaUsuarios");

        if(usuarios != null)
        {
            jsonCargado = JSON.parse(usuarios)
        }
        else
        {
            jsonCargado = Array<Pelicula>();
        }

        return jsonCargado;
    }


}

export enum genero
    {
        terror,
        comedia,
        amor,
        otro
    }
