import { Component, Input, OnInit  } from '@angular/core';
import { Pelicula, genero } from 'src/app/classes/pelicula';

@Component({
  selector: 'app-tabla-pelicula',
  templateUrl: './tabla-pelicula.component.html',
  styleUrls: ['./tabla-pelicula.component.css']
})

export class TablaPeliculaComponent implements OnInit
{
  @Input() listaPeliculas : Pelicula[];

  constructor()
  {
    this.listaPeliculas = [];
  }

  ngOnInit(): void 
  {

  }
}

<link rel="stylesheet" type="text/css" href="./tabla-pelicula.component.css">

<table>
    <thead>
        <th>Nombre</th>
        <th>Tipo</th>
        <th>Fecha de estreno</th>
        <th>Cantidad de publico</th>
        <th>Foto de portada</th>
    </thead>
    <tbody>
        <tr *ngFor="let pelicula of this.listaPeliculas">
            <td>{{pelicula.nombre}} </td>
            <td>{{pelicula.tipo}} </td>
            <td>{{pelicula.fechaEstreno}} </td>
            <td>{{pelicula.cantPublico}} </td>
            <td>{{pelicula.fotoPortada}} </td>
        </tr>
    </tbody>
</table>
