import { Component, Input, OnInit } from '@angular/core';
import { Pelicula, genero } from 'src/app/classes/pelicula';

@Component({
  selector: 'app-busqueda',
  templateUrl: './busqueda.component.html',
  styleUrls: ['./busqueda.component.css']
})
export class BusquedaComponent implements OnInit
{
  peliculas : Array<Pelicula> = new Array<Pelicula>();

  ngOnInit(): void 
  {
    this.peliculas = Pelicula.obtenerLocalStorage()
    alert(typeof(this.peliculas));
    if(this.peliculas == null)
    {
      this.peliculas = new Array<Pelicula>()
      let peliculaEjemplo1 = new Pelicula("peli1",genero.amor,new Date(),10,"asd");
      let peliculaEjemplo2 = new Pelicula("peli2",genero.terror,new Date(),2,"hola");
      let peliculaEjemplo3 = new Pelicula("peli3",genero.comedia,new Date(),53,"estonoesunafoto");

      this.peliculas.push(peliculaEjemplo1);
      this.peliculas.push(peliculaEjemplo2);
      this.peliculas.push(peliculaEjemplo3); 

      peliculaEjemplo1.guardarEnLocalStorage();
      peliculaEjemplo2.guardarEnLocalStorage();
      peliculaEjemplo3.guardarEnLocalStorage();
    }

  }
}
