import { Component, Input } from '@angular/core';
import { Pelicula } from 'src/app/classes/pelicula';

@Component({
  selector: 'app-detalle-pelicula',
  templateUrl: './detalle-pelicula.component.html',
  styleUrls: ['./detalle-pelicula.component.css']
})
export class DetallePeliculaComponent 
{

  @Input() peliculaSeleccionada : Pelicula

  constructor()
  {
    this.peliculaSeleccionada = new Pelicula("","",new Date(),0,"");
  }

  ngOnInit(): void 
  {

  }

}
<div class="detalle-pelicula">
    <h2>Detalles de la Película</h2>
    <div *ngIf="peliculaSeleccionada">
        <div>
            <strong>Nombre:</strong> {{ peliculaSeleccionada.nombre }}
        </div>
        <div class="tipo-y-fecha">
            <strong>Tipo:</strong> {{ peliculaSeleccionada.tipo }}
            <span class="fecha-estreno">Estreno: {{ peliculaSeleccionada.fechaEstreno | date: 'dd/MM/yyyy' }}</span>
        </div>
        <div>
            <strong>Foto de Portada:</strong>
            <img [src]="peliculaSeleccionada.fotoPortada" alt="Foto de Portada" style="max-width: 300px;">
        </div>
    </div>
    <div *ngIf="!peliculaSeleccionada">
        <p>Selecciona una película para ver los detalles.</p>
    </div>
</div>
