import { Component, Input } from '@angular/core';
import { Pelicula, genero } from 'src/app/classes/pelicula';

@Component({
  selector: 'app-tabla-pelicula',
  templateUrl: './tabla-pelicula.component.html',
  styleUrls: ['./tabla-pelicula.component.css']
})
export class TablaPeliculaComponent 
{
  @Input() peliculas : Pelicula[];

  constructor()
  {
    this.peliculas = [];

    let peliculaEjemplo1 = new Pelicula("peli1",genero.amor,new Date(),10,"asd");
    let peliculaEjemplo2 = new Pelicula("peli2",genero.terror,new Date(),2,"hola");
    let peliculaEjemplo3 = new Pelicula("peli3",genero.comedia,new Date(),53,"estonoesunafoto");

    this.peliculas.push(peliculaEjemplo1);
    this.peliculas.push(peliculaEjemplo2);
    this.peliculas.push(peliculaEjemplo3);
  }
}
<table>
    <thead>
        <th>Nombre</th>
        <th>Tipo</th>
        <th>Fecha de estreno</th>
        <th>Cantidad de publico</th>
        <th>Foto de portada</th>
    </thead>
    <tbody>
        <tr *ngFor="let pelicula of this.peliculas">
            <td>{{pelicula.nombre}} </td>
            <td>{{pelicula.tipo}} </td>
            <td>{{pelicula.fechaEstreno}} </td>
            <td>{{pelicula.cantPublico}} </td>
            <td>{{pelicula.fotoPortada}} </td>
        </tr>
    </tbody>
</table>
